
public class AritmetickiOperatori {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("5 + 2 = " + (5 + 2));
		System.out.println("5 - 2 = " + (5 - 2));
		System.out.println("5 * 2 = " + (5 * 2));
		System.out.println("5 / 2 = " + (5 / 2));
		System.out.println("5.0 / 2.0 = " + (5.0 / 2.0));
		System.out.println("5 % 2 = " + (5 % 2));

	}

}
